import React, { useState } from 'react';

export default function App() {
	const questions = [
		{
			questionText: 'Combien de litres d\'eau avez vous √† la maison ?',
			answerOptions: [
				{ answerText: 'un', answerScore: 1 },
				{ answerText: 'six', answerScore: 6 },
				{ answerText: 'dix', answerScore: 10 },
				{ answerText: 'vingt', answerScore: 20 },
			],
		},
		{
			questionText: 'Who is CEO of Tesla?',
			answerOptions: [
				{ answerText: 'Jeff Bezos', answerScore: 1 },
				{ answerText: 'Elon Musk', answerScore: 0 },
				{ answerText: 'Bill Gates', answerScore: 3 },
				{ answerText: 'Tony Stark', answerScore: 4 },
			],
		},
		{
			questionText: 'The iPhone was created by which company?',
			answerOptions: [
				{ answerText: 'Apple', answerScore: 1 },
				{ answerText: 'Intel', answerScore: 2 },
				{ answerText: 'Amazon', answerScore: 2 },
				{ answerText: 'Microsoft', answerScore:2 },
			],
		},
		{
			questionText: 'How many Harry Potter books are there?',
			answerOptions: [
				{ answerText: '1', answerScore: 3 },
				{ answerText: '4', answerScore: 1 },
				{ answerText: '6', answerScore: 1 },
				{ answerText: '7', answerScore: 2 },
			],
		},
	];

	const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [ecoScore, setEcoScore] = useState(0);

	const handleAnswerOptionClick = () => {
		//ajoute le nombre d'eco points au total
		setEcoScore(ecoScore + answerScore);
		console.log("Votre Eco Score est de ", ecoScore)
				//passe √† la question suivante et verifie si on est √† la derniere question 
		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	};
	return (

		// ECRAN d'AFFICHAGE 
		<div className='app'>
			<div>Votre score actuel {ecoScore}</div>		

			{showScore ? (
				//SI showScore is TRUE, on affiche le score final 
				<div className='score-section'>
					Vous avez marqu√© {ecoScore} eco points üëçüèª, f√©licitations ! 
				</div>
			) : (
				//SINON, on affiche les questions
				<>
					<div className='question-section'>
						<div className='question-count'>
							<span>Question {currentQuestion + 1}</span>/{questions.length}
						</div>
						<div className='question-text'>{questions[currentQuestion].questionText}</div>
					</div>
					<div className='answer-section'>
						{questions[currentQuestion].answerOptions.map((answerOption) => (
							<button onClick={() => handleAnswerOptionClick(answerOption.answerScore)}>{answerOption.answerText}</button>
						))}
					</div>
				</>
			)}

			<div className='add-question'>
				<p>Ajouter une question ?</p>

			</div>

		</div>
	);
}
