import React, { useState } from 'react';

export default function App() {
	const questions = [
		{
			questionText: 'Combien de litres d\'eau avez vous à la maison ?',
			answerOptions: [
				{ answerText: 'un', isCorrect: true, ecoScore: 1 },
				{ answerText: 'six', isCorrect: true, ecoScore: 6 },
				{ answerText: 'dix', isCorrect: true, ecoScore: 10 },
				{ answerText: 'vingt', isCorrect: true, ecoScore: 20 },
			],
		},
		{
			questionText: 'Who is CEO of Tesla?',
			answerOptions: [
				{ answerText: 'Jeff Bezos', isCorrect: true, ecoScore: 1 },
				{ answerText: 'Elon Musk', isCorrect: true, ecoScore: 0 },
				{ answerText: 'Bill Gates', isCorrect: true, ecoScore: 3 },
				{ answerText: 'Tony Stark', isCorrect: true, ecoScore: 4 },
			],
		},
		{
			questionText: 'The iPhone was created by which company?',
			answerOptions: [
				{ answerText: 'Apple', isCorrect: true, ecoScore: 1 },
				{ answerText: 'Intel', isCorrect: true, ecoScore: 2 },
				{ answerText: 'Amazon', isCorrect: true, ecoScore: 2 },
				{ answerText: 'Microsoft', isCorrect: true, ecoScore:2 },
			],
		},
		{
			questionText: 'How many Harry Potter books are there?',
			answerOptions: [
				{ answerText: '1', ecoScore: 3 },
				{ answerText: '4', ecoScore: 1 },
				{ answerText: '6', ecoScore: 1 },
				{ answerText: '7', ecoScore: 2 },
			],
		},
	];

	const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);
	const [ecoScore, setEcoScore] = useState(0);
	const palm = '🌴'; 

	const handleAnswerOptionClick = () => {
		//ajoute le nombre d'eco points au total
		setScore(score) 
		setEcoScore(score + ecoScore);
		console.log("Votre Eco Score est de ", ecoScore)

		//passe à la question suivante et verifie si on est à la derniere question 
		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	};
	return (
		<div className='app'>
			<div>Votre score actuel {ecoScore}</div>		
			{showScore ? (
				<div className='score-section'>
					Vous avez marqué {palm} {ecoScore} eco points 👍🏻, félicitations ! 
				</div>
			) : (
				<>
					<div className='question-section'>
						<div className='question-count'>
							<span>Question {currentQuestion + 1}</span>/{questions.length}
						</div>
						<div className='question-text'>{questions[currentQuestion].questionText}</div>
					</div>
					<div className='answer-section'>
						{questions[currentQuestion].answerOptions.map((answerOption) => (
							<button onClick={() => handleAnswerOptionClick(answerOption.isCorrect)}>{answerOption.answerText}</button>
						))}
					</div>
				</>
			)}

			<div className='add-question'>
				<p>Ajouter une question ?</p>

			</div>

		</div>
	);
}
