import React, { useState } from 'react';

export default function App() {
	const questions = [
		{
			questionText: 'Combien de litres d\'eau as tu √† la maison batard ?',
			answerOptions: [
				{ answerText: 'un litre', answerScore: 1 },
				{ answerText: 'six litres', answerScore: 6 },
				{ answerText: 'dix litres', answerScore: 10 },
				{ answerText: 'ving litres', answerScore: 20 },
			],
		},
		{
			questionText: 'Et combien de panneaux solaires encul√© ?',
			answerOptions: [
				{ answerText: '1', answerScore: 1 },
				{ answerText: '2', answerScore: 2 },
				{ answerText: '3', answerScore: 3 },
				{ answerText: '4', answerScore: 4 },
			],
		},
	];

	const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [ecoScore, setEcoScore] = useState(0);

	const handleAnswerOptionClick = (answerScore) => {
		//ajoute le nombre d'eco points au total
		setEcoScore(ecoScore + answerScore);

				//passe √† la question suivante et verifie si on est √† la derniere question 
		const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	};
	return (

		// ECRAN d'AFFICHAGE 
		<div className='app'>
			<div>Votre score actuel {ecoScore}</div>		

			{showScore ? (
				//SI showScore is TRUE, on affiche le score final 
				<div className='score-section'>
					Vous avez marqu√© {ecoScore} eco points üëçüèª, f√©licitations ! 
				</div>
			) : (
				//SINON, on affiche les questions
				<>
					<div className='question-section'>
						<div className='question-count'>
							<span>Question {currentQuestion + 1}</span>/{questions.length}
						</div>
						<div className='question-text'>{questions[currentQuestion].questionText}</div>
					</div>
					<div className='answer-section'>
						{questions[currentQuestion].answerOptions.map((answerOption) => (
							<button onClick={() => handleAnswerOptionClick(answerOption.answerScore)}>{answerOption.answerText}</button>
						))}
					</div>
				</>
			)}

			<div className='add-question'>
				<p>Ajouter une question ?</p>

			</div>

		</div>
	);
}
